from Crypto.Util.number import*
from gmpy2 import *
n 
c 
e = 13
import gmpy2
for i in range(10000000):
    m, is_true_root = gmpy2.iroot(i*n + c, e)
    if is_true_root:
        print(f"Found i = {i}")
        print("Message success: {}".format(bytearray.fromhex(format(m, 'x')).decode()))
        break
    else: continue

